mapper.update:
update WW_USER
set  USER_ID = #{userId},  USER_PASSWORD = #{userPassword}, USER_EMAIL = #{userEmail}, USER_NICKNAME = #{userNickname}, USER_PHONE_NUMBER = #{userPhoneNumber}
where USER_NUMBER = #{userNumber}

mapper.select:
SELECT COUNT(MENTOR_NUMBER)
FROM MENTOR
WHERE USER_NUMBER = #{userNumber}

SELECT USER_NICKNAME
FROM WW_USER
WHERE USER_NUMBER = #{userNumber}

mapper.delete:
DELETE FROM CAREERINFO_COMMENT WHERE USER_NUMBER = #{userNumber}

          OR CAREERINFO_NUMBER IN

               #{careerInfoNumber}

DELETE FROM CAREERINFO_LIKE WHERE USER_NUMBER = #{userNumber}

           OR CAREERINFO_NUMBER IN

               #{careerInfoNumber}

DELETE FROM QNA_COMMENT_UD WHERE USER_NUMBER = #{userNumber}

          OR  COMMENT_NUMBER IN

               #{qnaNumber}
